// Module for individual sugar crystal
module sugar_crystal(size = 1) {
    color([0.95, 0.95, 0.85])
    translate([0, 0, 0])
    scale([1, 1, 0.7])
    sphere(size/2);
}

// Module for cinnamon swirl pattern
module cinnamon_swirl() {
    color([0.5, 0.25, 0.1])
    linear_extrude(height = swirl_height, twist = 180) {
        for(i = [0:10:360]) {
            rotate([0, 0, i])
            translate([swirl_width + sin(i*3)*2, 0, 0])
            circle(d = 1);
        }
    }
}

// Main cereal piece body
module cereal_base() {
    // Main toast shape with slight curve
    color([0.8, 0.6, 0.4])
    union() {
        // Base rectangle
        translate([0, 0, 0])
        cube([cereal_width, cereal_depth, cereal_height], center = true);

        // Rounded edges using spheres
        translate([cereal_width/2-2, cereal_depth/2-2, 0])
        sphere(2);
        translate([-cereal_width/2+2, cereal_depth/2-2, 0])
        sphere(2);
        translate([cereal_width/2-2, -cereal_depth/2+2, 0])
        sphere(2);
        translate([-cereal_width/2+2, -cereal_depth/2+2, 0])
        sphere(2);
    }
}

// Sugar coating layer
module sugar_coating() {
    color([0.9, 0.9, 0.8])
    translate([0, 0, coating_thickness/2])
    difference() {
        cube([cereal_width+1, cereal_depth+1, coating_thickness], center = true);
        cube([cereal_width-1, cereal_depth-1, coating_thickness+1], center = true);
    }
}

// Cinnamon-sugar mixture pattern
module cinnamon_pattern() {
    color([0.6, 0.3, 0.15])
    for(x = [-cereal_width/2+3 : 3 : cereal_width/2-3]) {
        for(y = [-cereal_depth/2+3 : 3 : cereal_depth/2-3]) {
            translate([x, y, cereal_height/2 + coating_thickness/2])
            cylinder(h = coating_thickness, d = 1.5, $fn = 6);
        }
    }
}

// Surface texture for realistic bread appearance
