    color([0.75, 0.55, 0.35])
    translate([0, 0, -cereal_height/2+1])
    for(i = [0:2:cereal_width-2]) {
        for(j = [0:2:cereal_depth-2]) {
            translate([i - cereal_width/2, j - cereal_depth/2, 0])
            sphere(0.3);
        }
    }
}

// Assembly of complete cereal piece
module cereal_piece() {
    union() {
        // Main body
        cereal_base();

        // Bread texture on bottom
        bread_texture();

        // Sugar coating on top
        translate([0, 0, cereal_height/2 + coating_thickness/2])
        sugar_coating();

