// Main rice cake body - slightly domed top with rounded edges
module rice_cake_body() {
    color("floralwhite")
    difference() {
        // Main cylindrical base
        cylinder(h=height, d=diameter, $fn=100);

        // Create domed top by subtracting sphere
        translate([0, 0, height - diameter/4])
            sphere(d=diameter*1.1, $fn=100);

        // Round bottom edge
        translate([0, 0, -edge_rounding])
            cylinder(h=height+edge_rounding*2, d=diameter-edge_rounding*2, $fn=100);

        // Flatten very bottom
        translate([0, 0, -1])
            cube([diameter, diameter, 2], center=true);
    }
}

// Cross-hatch texture pattern on top surface
module top_texture() {
    color("ivory")
    for(i=[-diameter/2+5:diameter/10:diameter/2-5]) {
        // Horizontal lines
        translate([0, i, height-0.5])
            cube([diameter-10, 0.5, 0.5], center=true);

        // Vertical lines
        translate([i, 0, height-0.5])
            cube([0.5, diameter-10, 0.5], center=true);
    }
}

// Sesame seeds scattered on top
module sesame_seeds() {
    color("darkslategray")
    for(i=[0:sesame_count-1]) {
        // Random placement using golden ratio for distribution
        angle = i * 137.508;
        radius = sqrt(i) * (diameter/2-5)/sqrt(sesame_count);

        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            height + 0.75
        ])
            scale([1, 0.7, 0.5])
                sphere(d=sesame_size, $fn=8);
    }
}

