// Coconut flour texture parameters
grain_size = 1.5;
grain_variance = 0.3;
grain_count = 150;

// Create the coconut flour pile
module coconut_flour_pile() {
    color([0.95, 0.90, 0.82]) {
        // Base pile shape
        difference() {
            // Main elliptical pile
            scale([1.2, 1, 0.8])
                sphere(d = flour_diameter, $fn = 64);

            // Flatten top slightly
            translate([0, 0, flour_height * 0.6])
                cube([flour_diameter * 2, flour_diameter * 2, flour_diameter], center = true);
        }

        // Add surface texture
        for(i = [0:grain_count - 1]) {
            // Random position on surface
            angle = rands(0, 360, 1)[0];
            radius = rands(0, flour_diameter/2.2, 1)[0];
            height = rands(0, flour_height * 0.7, 1)[0];

            // Calculate position on ellipsoid surface
            x = cos(angle) * radius * 1.2;
            y = sin(angle) * radius;
            z = flour_height * 0.8 * sqrt(1 - (x/(flour_diameter/1.2*1.2))^2 - (y/(flour_diameter/2))^2);

            // Random grain size
            size = grain_size + rands(-grain_variance, grain_variance, 1)[0];

            // Place individual grains
            translate([x, y, z])
                sphere(d = size, $fn = 8);
        }

        // Add some larger clumps
        for(i = [0:20]) {
            angle = rands(0, 360, 1)[0];
            radius = rands(0, flour_diameter/2.5, 1)[0];
            x = cos(angle) * radius * 1.2;
            y = sin(angle) * radius;
            z = rands(5, flour_height * 0.6, 1)[0];

            translate([x, y, z])
                scale([1.5, 1.2, 0.8])
                    sphere(d = grain_size * 3, $fn = 12);
        }
    }
}

