    color([0.95, 0.90, 0.82]) {
        // Scoop shape - hemisphere
        translate([flour_diameter/4, -flour_diameter/4, flour_height * 0.5])
            scale([1, 1, 0.5])
                sphere(d = flour_diameter/3, $fn = 32);

        // Scoop edge detail
        translate([flour_diameter/4, -flour_diameter/4, flour_height * 0.5])
            difference() {
                cylinder(h = 2, d = flour_diameter/3 + 2, $fn = 32);
