// Main flour pile module
module cassava_flour_pile() {
    // Base mound shape
    difference() {
        // Main conical pile
        union() {
            // Central core
            cylinder(h = pile_height, r1 = pile_radius, r2 = 5, $fn = 48);

            // Add irregularity to sides with noise
            for(i = [0:5:360]) {
                rotate([0, 0, i]) {
                    translate([pile_radius - 2, 0, 0]) {
                        cylinder(h = pile_height * 0.3, r1 = 3, r2 = 1, $fn = 8);
                    }
                }
            }
        }

        // Carve out center to make more natural
        translate([0, 0, -1]) {
            cylinder(h = pile_height + 2, r = 8, $fn = 32);
        }

        // Add slight depression on top
        translate([0, 0, pile_height - 2]) {
            sphere(d = 12);
        }
