    // Randomized small spheres for powder texture
    scale([1 + (rand() - 0.5) * 0.3, 1 + (rand() - 0.5) * 0.3, 1 + (rand() - 0.5) * 0.3]) {
        sphere(d = grain_size, $fn = 8);
    }
