// Generate random number between 0-1
function rand() = rands(0, 1, 1)[0];

// Create surface texture layer
module surface_texture(height_offset) {
    for(i = [0:1:grains_per_layer]) {
        angle = rands(0, 360, 1)[0];
        radius = rands(0, pile_radius * (1 - height_offset/pile_height), 1)[0];
        x = cos(angle) * radius;
        y = sin(angle) * radius;
        z = height_offset + rands(-1, 1, 1)[0];

        translate([x, y, z]) {
            flour_grain();
        }
    }
}

// Create realistic flour spill pattern
module flour_spill() {
    // Main pile
    color(flour_color) {
        cassava_flour_pile();
    }

    // Surface texture layers
    color(flour_color) {
        for(h = [5:3:pile_height - 5]) {
            surface_texture(h);
        }
    }

    // Small spill around base
    color(flour_color) {
        for(i = [0:20:360]) {
            rotate([0, 0, i]) {
                translate([pile_radius + rands(0, 8, 1)[0], 0, 0]) {
                    scale([1, 1, 0.3]) {
                        sphere(d = rands(2, 6, 1)[0], $fn = 12);
                    }
                }
            }
        }
    }

    // Subtle color variation for realism
