// Helper module for rounded cuboid
module rounded_cuboid(size, radius) {
    hull() {
        for(x=[0, size[0]]) for(y=[0, size[1]]) for(z=[0, size[2]]) {
            translate([x + (x==0?radius:-radius), 
                      y + (y==0?radius:-radius), 
                      z + (z==0?radius:-radius)]) 
                sphere(r=radius);
        }
    }
}

