            // Main leaf shape
            offset(r = 2) {
                polygon(points = [
                    [0, 0], [10, 5], [20, 3], [30, 8],
                    [35, 15], [30, 25], [20, 30], [10, 28],
                    [2, 20], [0, 10]
                ]);
