            // Vein indentations
            for (i = [1:9]) {
                translate([i*3.5, 10 + sin(i*30)*3, 0])
                    circle(r = 0.8);
            }
        }
    }
}

module crinkled_leaf(x_pos, y_pos, z_pos, rot, scale_factor) {
    // Individual crinkled spinach leaf
    translate([x_pos, y_pos, z_pos]) {
        rotate([0, 0, rot]) {
            scale([scale_factor, scale_factor, 1]) {
                union() {
                    leaf_base();

                    // Add crinkles with small spheres
                    for (i = [0:5]) {
                        translate([
                            15 + sin(i*45)*8,
                            15 + cos(i*45)*6,
                            0.6
                        ]) {
                            scale([1.2, 0.8, 0.4])
                                sphere(r = 2.5, $fn = 8);
