                        translate([
                            17 + cos(angle) * 15,
                            15 + sin(angle) * 12,
                            0.9
                        ]) {
                            scale([0.4, 0.4, 0.6])
                                sphere(r = 1.5, $fn = 6);
                        }
                    }
                }
            }
        }
    }
}

module spinach_bunch() {
