                cylinder(h = 25, r1 = 3, r2 = 2, $fn = 8);
            }
        }
    }

    // Multiple leaves radiating from stem
    for (i = [0:7]) {
        angle = i * 45;
        radius = 8 + (i % 2) * 5;
        crinkled_leaf(
            cos(angle) * radius,
            sin(angle) * radius,
            20 + sin(i*30) * 3,
            angle + 15,
            1.2 - (i % 3) * 0.2
        );
