// Realistic Cabbage - Medium Complexity
// Outer leaves and inner layers with proper structure for 3D printing

$fn = 32;

// Overall cabbage dimensions
cabbage_diameter = 80;
cabbage_height = 60;

// Main cabbage module
module cabbage() {
    color("lightgreen")
    union() {
        // Outer leaves - curved and overlapping
        for (i = [0:7]) {
            rotate([0, 0, i * 45])
            translate([0, 0, -5])
            scale([1.0 - i * 0.05, 1.0 - i * 0.05, 1])
            outer_leaf(i);
        }

        // Inner cabbage head - tighter layers
        inner_cabbage();

        // Stem base
        stem();
    }
}

// Individual outer leaf with realistic curve
module outer_leaf(index) {
    leaf_length = cabbage_diameter * 0.7 - index * 3;
    leaf_width = cabbage_diameter * 0.5 - index * 2;

    // Create wavy leaf surface
    difference() {
        // Main leaf body
        union() {
            // Central part of leaf
            translate([0, 0, 5])
            scale([leaf_width/leaf_length, 1, 0.3])
            sphere(d = leaf_length);

            // Leaf edges - create wavy effect
            for (j = [0:6]) {
                rotate([0, 0, j * 30])
                translate([leaf_length * 0.3, 0, 2])
                scale([0.3, 1, 0.5])
                sphere(d = leaf_length * 0.4);
            }
        }

        // Remove center to create shell
        translate([0, 0, -5])
        cylinder(h = 20, r = leaf_length * 0.3, $fn = 16);
    }
}

// Inner cabbage structure - layered spheres
module inner_cabbage() {
    for (layer = [0:4]) {
        // Each layer is slightly smaller and higher
        color([0.8 - layer * 0.1, 1.0 - layer * 0.05, 0.5 + layer * 0.05])
        translate([0, 0, layer * 6])
        scale([1 - layer * 0.15, 1 - layer * 0.15, 0.8])

        // Create faceted surface for inner leaves
        intersection() {
            sphere(d = cabbage_diameter - layer * 10);

            // Create the layered effect with slight rotation
            rotate([layer * 3, layer * 3, 0])
            for (i = [0:11]) {
                rotate([0, 0, i * 30])
                translate([cabbage_diameter * 0.3, 0, 0])
                cube([cabbage_diameter, cabbage_diameter, cabbage_diameter], center = true);
            }
        }
    }
}

// Stem at bottom
module stem() {
