// Realistic Lettuce Head in OpenSCAD
// Medium complexity with basic primitives and transformations

// Global parameters
lettuce_height = 60;
lettuce_radius = 45;
stem_height = 8;
stem_radius = 15;

// Color definitions
$fn = 30;

module lettuce_stem() {
    // Central stem structure
    color("forestgreen")
    union() {
        // Main stem cylinder
        cylinder(h=stem_height, r=stem_radius, $fn=20);

        // Bottom root-like details
        for(i=[0:5]) {
            rotate([0, 0, i*60])
            translate([stem_radius-2, 0, -1])
            rotate([90, 0, 0])
            cylinder(h=4, r1=3, r2=1, $fn=8);
        }
    }
}

module leaf_layer(height, radius, angle, layers) {
    // Individual leaf with realistic curves
    color("limegreen")
    rotate([0, 0, angle])
    translate([0, 0, height])
    scale([1, 0.8, 1])
    difference() {
        // Main leaf body
        union() {
            // Elliptical leaf shape
            scale([1.2, 1, 0.3])
            sphere(r=radius);

            // Leaf tip extension
            translate([0, 0, radius*0.3])
            scale([0.8, 0.6, 0.5])
            sphere(r=radius*0.5);
        }

        // Vein indentations
        for(i=[-2:2]) {
            translate([i*radius/4, 0, -1])
            scale([0.1, 1, 0.5])
            sphere(r=radius);
        }

        // Leaf edge serrations
        for(i=[0:layers*3]) {
            rotate([0, 0, i*360/(layers*3)])
            translate([radius*0.9, 0, 0])
            scale([0.15, 0.1, 0.3])
            sphere(r=radius/5);
        }
    }
}

module lettuce_head() {
    // Main lettuce structure
    union() {
        // Base stem
        lettuce_stem();

        // Layered leaves
        for(i=[0:8]) {
            // Progressive leaf sizes
            leaf_size = lettuce_radius * (1 - i*0.08);
            leaf_height = stem_height + i*5;

            // Create multiple leaves per layer
            for(j=[0:i]) {
                leaf_layer(
                    leaf_height, 
                    leaf_size, 
                    j*(360/(i+1)) + (i%2)*15, 
                    i+1
                );
            }

            // Inner compact layers
            if(i > 5) {
                translate([0, 0, leaf_height+2])
                scale([0.7 - (i-5)*0.1, 0.7 - (i-5)*0.1, 0.5])
                sphere(r=lettuce_radius*0.8);
            }
        }

        // Top center detail
        color("darkgreen")
        translate([0, 0, stem_height + 45])
        scale([0.3, 0.3, 0.4])
        sphere(r=20);
    }
}

// Create the complete lettuce
lettuce_head();

