module artichoke_leaf(h, r, angle, scale_factor) {
    // Single leaf with proper orientation for printing
    rotate([0,0,angle])
        translate([0,0,h])
            rotate([atan2(h, r),0,0])
                scale([scale_factor, scale_factor, scale_factor])
                    difference() {
                        // Main leaf shape
                        union() {
                            // Leaf tip
                            translate([0,0,8])
                                scale([0.3,1,1])
                                    sphere(d=10, $fn=20);

                            // Leaf body
                            hull() {
                                translate([0,-3,0])
                                    cylinder(h=0.1, d=1, $fn=4);
                                translate([0,3,0])
                                    cylinder(h=0.1, d=1, $fn=4);
                                translate([0,0,8])
                                    cylinder(h=0.1, d=1, $fn=4);
                            }
                        }

                        // Leaf thickness
                        scale([0.7,0.7,0.7])
                            sphere(d=15, $fn=20);
                    }
}

module artichoke_leaves() {
    // Generate layers of leaves from bottom to top
    for(layer=[0:6]) {
        layer_height = 12 + layer * 8;
        layer_radius = 18 - layer * 2;
        num_leaves = 12 - layer;

        for(i=[0:num_leaves-1]) {
            angle = i * (360 / num_leaves) + (layer % 2) * 15;
            scale_factor = 0.8 - layer * 0.08;
            artichoke_leaf(layer_height, layer_radius, angle, scale_factor);
        }
    }

    // Top small leaves (bracts)
    for(i=[0:7]) {
        color("purple") {
            translate([0,0,62])
                rotate([0,0,i*45])
                    rotate([75,0,0])
                        scale([0.4,0.4,0.4])
                            difference() {
                                cylinder(h=8, d1=6, d2=2, $fn=3);
                                translate([0,0,-0.5])
                                    cylinder(h=9, d1=5, d2=1, $fn=3);
                            }
        }
    }
}

