// Main cauliflower head
module cauliflower_head() {
    // Central core
    color(cauliflower_color) {
        sphere(d=total_width/2);

        // First ring of florets
        for(i = [0:floret_count-1]) {
            angle = i * (360/floret_count);
            z = 8;
            radius = total_width/3;

            floret(
                size = total_width/5,
                position = [cos(angle)*radius, sin(angle)*radius, z],
                angle = [atan2(sin(angle)*radius, cos(angle)*radius), 0, 0]
            );
        }

        // Second ring of florets (smaller)
        for(i = [0:floret_count+4-1]) {
            angle = i * (360/(floret_count+4));
            z = 15;
            radius = total_width/2.2;

            floret(
                size = total_width/6,
                position = [cos(angle)*radius, sin(angle)*radius, z],
                angle = [atan2(sin(angle)*radius, cos(angle)*radius), 0, 0]
            );
        }

        // Top florets
        for(i = [0:5]) {
            angle = i * 60;
            floret(
                size = total_width/7,
                position = [0, 0, total_width/2.5],
                angle = [0, 0, angle]
            );
        }
    }
}

