    // Main leaf body with lobes
    union() {
        // Central leaf body
        hull() {
            translate([0, 0, 0]) sphere(d=leaf_width * 0.8);
            translate([leaf_length * 0.3, 0, 0]) sphere(d=leaf_width * 0.7);
            translate([leaf_length * 0.7, 0, 0]) sphere(d=leaf_width * 0.4);
        }

        // Side lobes
        for (side = [-1, 1]) {
            translate([leaf_length * 0.4, side * leaf_width * 0.3, 0])
                rotate([0, 0, side * 30])
                scale([0.6, 0.4, 0.3])
                sphere(d=leaf_width * 0.7);

            translate([leaf_length * 0.6, side * leaf_width * 0.2, 0])
                rotate([0, 0, side * 45])
                scale([0.4, 0.3, 0.2])
                sphere(d=leaf_width * 0.5);
        }

        // Stem
        color([0.25, 0.55, 0.25])
        translate([-stem_length, 0, -1])
            cylinder(h=stem_length, d=stem_diameter, $fn=8);
    }
}

module leaf_veins() {
    color([0.1, 0.35, 0.1, 0.7])

    // Main central vein
    translate([0, 0, 0.5])
        scale([leaf_length * 0.9, 1, 0.3])
        sphere(d=leaf_width * 0.08);

    // Secondary veins
    for (i = [1:5]) {
        angle = 30 + i * 8;
        length = leaf_length * 0.4 - i * 5;

        // Left veins
        translate([i * leaf_length * 0.15, 0, 0.5])
            rotate([0, 0, angle])
            scale([length, 0.5, 0.2])
            sphere(d=leaf_width * 0.05);

        // Right veins
        translate([i * leaf_length * 0.15, 0, 0.5])
            rotate([0, 0, -angle])
            scale([length, 0.5, 0.2])
            sphere(d=leaf_width * 0.05);
