    color([0.12, 0.42, 0.12, 0.6])
    for (x = [0.1:0.15:0.9]) {
        for (y = [-0.4:0.1:0.4]) {
            pos_x = x * leaf_length;
            pos_y = y * leaf_width;
            height = 0.2 + sin(x * 10) * 0.1;

            translate([pos_x, pos_y, 0.8])
                sphere(d=0.8);
