        rotate([30, 0, 0]) 
        scale([0.7, 0.9, 0.5]) 
        sphere(r = 8 - layer_num * 0.6, $fn = 20);

        // Leaf veins - subtle ridges
        for (k = [0:2]) {
            rotate([0, 0, (k - 1) * 25]) 
            translate([0, 0, -0.5]) 
            scale([0.1, 1, 0.05]) 
