module collard_leaf() {
    // Main stem structure
    color(color_stem) 
    translate([0,-40,0])
    rotate([0,5,0])
    linear_extrude(height=stem_thickness)
    polygon(points=[
        [0,0],
        [-2,-3],
        [-2,-85],
        [0,-88],
        [2,-85],
        [2,-3]
    ]);

    // Main leaf body
    color(color_leaf)
    translate([0,-5,leaf_thickness/2])
    linear_extrude(height=leaf_thickness)
    polygon(points=[
        [0,0],
        [15,-10],
        [25,-25],
        [30,-45],
        [28,-65],
        [20,-80],
        [10,-90],
        [-10,-90],
        [-20,-80],
        [-28,-65],
        [-30,-45],
        [-25,-25],
        [-15,-10]
    ]);

    // Secondary leaf on left
    color(color_leaf)
    translate([-20,-25,leaf_thickness/2])
    rotate([-15,0,-25])
    linear_extrude(height=leaf_thickness)
    polygon(points=[
        [0,0],
        [8,-8],
        [12,-20],
        [10,-35],
        [5,-45],
        [-2,-50],
        [-8,-45],
        [-12,-35],
        [-10,-20],
        [-8,-8]
    ]);

    // Secondary leaf on right
    color(color_leaf)
    translate([20,-30,leaf_thickness/2])
    rotate([-10,0,20])
    linear_extrude(height=leaf_thickness)
    polygon(points=[
        [0,0],
        [10,-10],
        [15,-25],
        [12,-40],
        [8,-50],
        [0,-55],
        [-8,-50],
        [-12,-40],
        [-10,-25],
        [-10,-10]
    ]);

    // Vein structure
    module vein_line(length, width, angle) {
        color(color_vein)
        rotate([0,0,angle])
        translate([0,-length/2,leaf_thickness+0.1])
        linear_extrude(height=0.3)
        polygon(points=[
            [-width/2, -length/2],
            [width/2, -length/2],
            [width/2, length/2],
            [-width/2, length/2]
        ]);
    }

    // Main veins
    vein_line(85, 1.2, 0);
    vein_line(40, 0.8, -25);
    vein_line(35, 0.8, 25);
    vein_line(30, 0.6, -45);
    vein_line(25, 0.6, 45);
    vein_line(20, 0.5, -60);
    vein_line(20, 0.5, 60);

    // Smaller veins using loop
    for(i=[-4:4]) {
        if(i != 0) {
            angle = 15 * i;
            length = 20 - abs(i)*2;
            vein_line(length, 0.3, angle);
        }
    }

    // Leaf surface texture
    module texture_dot(x,y,size) {
        color([0.1,0.35,0.1])
        translate([x,y,leaf_thickness+0.05])
        cylinder(h=0.1, r=size, $fn=8);
    }

    // Add surface texture
    for(y=[-75:-5:5]) {
        for(x=[-25:5:25]) {
            if(norm([x,y-45]) < 30) {
                texture_dot(x+sin(y*3)*2, y, 0.3+0.2*sin(x*2+y));
            }
        }
    }

    // Leaf edge detail
    color(color_leaf)
    translate([0,-45,leaf_thickness])
    difference() {
        cylinder(h=0.4, r=35);
        cylinder(h=0.5, r=32);
    }

    // Slight curl for realism
    color(color_leaf)
    translate([0,-45,0])
    rotate([5,0,0])
    translate([0,0,leaf_thickness])
    linear_extrude(height=leaf_thickness)
    circle(r=2);
}

