        translate([leaf_length - 2, 0, 0])
        scale([0.3, 0.4, 0.3])
        sphere(d = leaf_width * 0.7, $fn = 15);

        // Add leaf thickness
        translate([leaf_length/2, 0, -leaf_thickness/2])
        cube([leaf_length, leaf_width * 0.8, leaf_thickness], center = true);
