        for(i = [1:1:5]) {
            height = i * stem_length/6;
            angle = 30 + i * 5;
            rotation = i * 137.5; // Golden angle for natural look

            // Opposite branches
            branch(height, angle, rotation);
            branch(height, angle, rotation + 180);

