    height = 15 + (index % 2) * 5;

    rotate([0, 0, angle]) {
        translate([0, 22, 25]) {
            rotate([90 - height/2, 0, 0]) {
                // Curved stem
                cylinder(h=height, d1=4, d2=2.5, $fn=8);

                // Leaf attachment point
                translate([0, 0, height]) 
                    sphere(d=3, $fn=8);
