    angle = index * 60;
    leaf_color = [0.15, 0.4, 0.1]; // Dark green

    color(leaf_color) {
        rotate([0, 0, angle]) {
            translate([0, 25, 28 + (index % 2) * 3]) {
                rotate([70, 0, 0]) {
                    scale([1.5, 1, 0.3]) {
                        // Main leaf body
