                        translate([0, 12, 0]) 
                            scale([0.5, 1, 0.5]) 
                                sphere(d=15, $fn=8);
                    }
                }
            }
        }
    }
}

module surface_texture() {
    // Small bumps on kohlrabi surface
    for(i = [0:5:359]) {
        for(j = [0:20:160]) {
            rotate([0, j, i]) {
                translate([28, 0, 0]) {
                    scale([0.5, 0.5, 0.3]) 
                        sphere(d=2, $fn=8);
                }
            }
        }
    }
}

