top_color = [0.2, 0.4, 0.1];   // Dark green

// Main body parameters
body_height = 60;
body_width = 70;
top_offset = 45;

// Create the main rutabaga body
module rutabaga_body() {
    color(root_color) {
        // Main bulb shape using sphere with scaling
        translate([0, 0, 0])
            scale([1, 0.9, 1.1])
                sphere(d=body_width, $fn=64);

        // Add bottom bulge for more realistic shape
        translate([0, 0, -body_height/4])
            scale([0.8, 0.8, 0.6])
                sphere(d=body_width * 0.9, $fn=64);
    }
}

// Create surface texture (root vegetable bumps)
module surface_texture() {
    color(root_color) {
        for(i = [0:20]) {
            // Random placement of small bumps on surface
            phi = rands(0, 360, 1)[0];
            theta = rands(0, 180, 1)[0];
            r = body_width/2;

            x = r * cos(phi) * sin(theta);
            y = r * sin(phi) * sin(theta);
            z = r * cos(theta) * 0.8;

            // Skip bumps near top
            if(z < top_offset - 10) {
                translate([x, y, z]) {
                    scale([1, 1, 0.8])
                        sphere(d=3 + rands(0, 2, 1)[0], $fn=8);
                }
            }
        }
    }
}

// Create the leafy top part
module leafy_top() {
    color(top_color) {
        // Central stem
        translate([0, 0, top_offset])
            cylinder(h=15, d1=8, d2=5, $fn=8);

        // Generate multiple leaves radiating from center
        for(i = [0:7]) {
            rotate([0, 0, i * 45]) {
                // Each leaf
                translate([5, 0, top_offset + 5])
                    rotate([90, 0, 0])
                        scale([1, 0.3, 1])
                            cylinder(h=20, d1=10, d2=2, $fn=6);

                // Secondary leaf layer
                translate([7, 3, top_offset + 3])
                    rotate([90, -10, 30])
                        scale([0.8, 0.25, 1])
                            cylinder(h=15, d1=8, d2=1, $fn=6);
            }
        }
    }
}

// Create root scars at bottom
module root_scars() {
    color([0.5, 0.3, 0.1]) {
        for(i = [0:4]) {
            angle = i * 72;
            translate([
                cos(angle) * 15,
                sin(angle) * 15,
                -body_height/2 + 5
            ]) {
                rotate([90, 0, angle])
                    cylinder(h=5, d1=3, d2=2, $fn=6);
            }
        }
    }
}

// Main assembly
