// Daikon root module - tapered cylinder with slight curve
module daikon_root() {
    color("#F5F5DC") // Off-white color for daikon flesh

    // Create tapered root with slight curve using hulls
    hull() {
        translate([0, 0, 0]) 
            sphere(d=root_tip_d, $fn=16);
        translate([0, 0, root_length * 0.3]) 
            scale([1.2, 1.1, 1]) sphere(d=root_tip_d * 1.8, $fn=16);
    }

    hull() {
        translate([0, 0, root_length * 0.3]) 
            scale([1.2, 1.1, 1]) sphere(d=root_tip_d * 1.8, $fn=16);
        translate([0, -2, root_length * 0.7]) 
            sphere(d=root_top_d * 0.8, $fn=20);
    }

    hull() {
        translate([0, -2, root_length * 0.7]) 
            sphere(d=root_top_d * 0.8, $fn=20);
        translate([0, -3, root_length]) 
            sphere(d=root_top_d, $fn=24);
    }

    // Add subtle root texture with small bumps
    for(i = [1:5:root_length-5]) {
        rotate([0, 0, i * 137.5]) {
            translate([root_tip_d/2 + i * 0.3, 0, i]) 
                sphere(d=1.5, $fn=8);
        }
    }
}

// Leaf module - elongated ellipsoid
module leaf(length, width_factor) {
    color("#90EE90") // Light green for fresh leaves
    scale([width_factor, 1, 1]) 
        rotate([90, 0, 0]) 
            cylinder(h=length, d1=2, d2=0.5, $fn=8);
}

// Leaf cluster at top
module leaf_cluster() {
    translate([0, -3, root_length]) {
        // Create multiple leaves radiating from center
        for(i = [0:leaf_count-1]) {
            rotate([0, 0, i * (360/leaf_count)]) {
                rotate([20 + (i%2)*10, 0, 0]) {
                    leaf(leaf_length + (i%3)*5, 0.4 + (i%2)*0.2);
                }
            }
        }

        // Add inner smaller leaves
        for(j = [0:leaf_count/2-1]) {
            rotate([0, 0, j * (360/(leaf_count/2)) + 22.5]) {
                rotate([30, 0, 0]) {
                    leaf(leaf_length * 0.7, 0.3);
                }
            }
        }
    }
}

// Add root hairs/fibrous texture near top
module root_details() {
    color("#E6E6FA") // Slightly darker white for details

    // Create slight ring pattern near top
    for(z = [root_length-20:5:root_length-5]) {
        rotate([0, 0, 15]) {
            difference() {
                cylinder(h=2, d=root_top_d * (1 - (root_length - z)/root_length * 0.3), $fn=32);
                translate([0, 0, -0.1]) 
                    cylinder(h=2.2, d=root_top_d * (1 - (root_length - z)/root_length * 0.3) - 1, $fn=32);
            }
        }
    }

    // Add small surface imperfections
    for(i = [1:8]) {
        theta = i * 45;
        for(j = [1:4]) {
            z_pos = root_length - j * 15;
            r = root_top_d * (1 - (root_length - z_pos)/root_length * 0.7) * 0.45;
            translate([r * cos(theta), r * sin(theta), z_pos]) 
                sphere(d=0.8, $fn=6);
        }
    }
}

// Main assembly
union() {
    daikon_root();
    leaf_cluster();
    root_details();
}

