// Realistic chive model for 3D printing
// Medium complexity with natural curves and texture

// Color definitions
color_chive_dark = [0.15, 0.4, 0.15];
color_chive_light = [0.2, 0.5, 0.2];
color_tip = [0.4, 0.6, 0.2];

// Main chive stem module with natural curve
module chive_stem(length, thickness, start_angle, end_angle) {
    // Create curved stem using hull of spheres
    segments = 12;
    sphere_size = thickness;

    for(i = [0:segments-1]) {
        t = i / (segments - 1);
        angle = start_angle + t * (end_angle - start_angle);
        x_pos = sin(angle) * length * 0.3 * t;
        y_pos = cos(angle) * length * 0.3 * t;
        z_pos = length * t;

        translate([x_pos, y_pos, z_pos])
            sphere(sphere_size * (1 - t * 0.3), $fn=8);
    }
}

// Create hollow tube for stem
module chive_tube(length, thickness) {
    difference() {
        // Outer tube
        cylinder(h=length, r1=thickness, r2=thickness * 0.7, $fn=16);

        // Inner hollow
        translate([0, 0, -1])
            cylinder(h=length+2, r1=thickness-0.8, r2=thickness*0.7-0.8, $fn=16);
    }
}

// Add texture rings along the stem
module stem_texture(length, thickness) {
    rings = floor(length / 3);
    for(i = [0:rings-1]) {
        z_pos = i * (length / rings);
        scale_factor = 1 - (z_pos / length) * 0.3;

        translate([0, 0, z_pos])
            difference() {
                cylinder(h=1, r=thickness * scale_factor + 0.2, $fn=16);
                cylinder(h=2, r=thickness * scale_factor, $fn=16);
            }
    }
}

// Create the tapered tip
module chive_tip(length, base_thickness) {
    color(color_tip) {
        translate([0, 0, length])
            cylinder(h=length * 0.15, r1=base_thickness * 0.7, r2=0, $fn=8);
    }
}

// Generate multiple chives with variations
module chive_cluster(count, base_x, base_y) {
    for(i = [0:count-1]) {
        angle = 360 * i / count + (i % 2 == 0 ? 15 : -15);
        length = 70 + rand(i) * 20;
        thickness = 3.5 + rand(i+5) * 1;

        translate([base_x, base_y, 0])
        rotate([0, 0, angle])
        translate([sin(angle) * 5, 0, 0]) {
            color(color_chive_dark) {
                // Main stem structure
                rotate([rand(i+10) * 5 - 2.5, rand(i+15) * 10 - 5, 0])
                    chive_tube(length, thickness);

                // Texture rings
                rotate([rand(i+10) * 5 - 2.5, rand(i+15) * 10 - 5, 0])
                    stem_texture(length, thickness);

                // Curved accent
                color(color_chive_light) {
                    translate([0, thickness/2, 0])
                        rotate([90, 0, 0])
                            chive_stem(length * 0.6, thickness * 0.5, 0, 30);
                }
            }

            // Tip
            rotate([rand(i+10) * 5 - 2.5, rand(i+15) * 10 - 5, 0])
                chive_tip(length, thickness * 0.7);
        }
    }
}

