// Main seaweed sheet with wavy edges
difference() {
    // Base sheet
    union() {
        // Central flat portion
        translate([0, 0, -sheet_thickness/2])
            cube([sheet_length - 4, sheet_width - 4, sheet_thickness], center=true);

        // Create wavy edges
        for(i = [0:wave_frequency-1]) {
            // Long edges
            translate([i * (sheet_length/wave_frequency) - sheet_length/2 + 2, sheet_width/2 - 1, 0])
                rotate([0, 0, 45])
                scale([1, 0.5, 1])
                cylinder(h=sheet_thickness, r1=wave_amplitude/2, r2=wave_amplitude/2);

            translate([i * (sheet_length/wave_frequency) - sheet_length/2 + 2, -sheet_width/2 + 1, 0])
                rotate([0, 0, -45])
                scale([1, 0.5, 1])
                cylinder(h=sheet_thickness, r1=wave_amplitude/2, r2=wave_amplitude/2);

            // Short edges
            translate([sheet_length/2 - 1, i * (sheet_width/wave_frequency) - sheet_width/2 + 2, 0])
                rotate([0, 0, -45])
                scale([0.5, 1, 1])
                cylinder(h=sheet_thickness, r1=wave_amplitude/2, r2=wave_amplitude/2);

            translate([-sheet_length/2 + 1, i * (sheet_width/wave_frequency) - sheet_width/2 + 2, 0])
                rotate([0, 0, 45])
                scale([0.5, 1, 1])
                cylinder(h=sheet_thickness, r1=wave_amplitude/2, r2=wave_amplitude/2);
        }
    }

    // Cut out some holes to simulate seaweed texture
    for(x = [-sheet_length/2 + 5:5:sheet_length/2 - 5]) {
        for(y = [-sheet_width/2 + 5:5:sheet_width/2 - 5]) {
            translate([x + sin(x*3)*1.2, y + cos(y*2)*1.2, 0])
                cylinder(h=sheet_thickness+1, r=0.3 + 0.2*sin(x+y), $fn=6);
