union() {
    for(i = [1:20]) {
        angle = i * 137.5;
        radius = sqrt(i) * 2.5;
        tx = cos(angle) * radius;
        ty = sin(angle) * radius;
        if(abs(tx) < sheet_length/2 - 3 && abs(ty) < sheet_width/2 - 3) {
            translate([tx, ty, sheet_thickness/2])
                scale([1, 1, 0.3])
                sphere(r=0.3 + 0.2*(sin(i*2) + 1)/2);
        }
    }

    // Add some wrinkles/folds
    for(i = [0:3]) {
        rotate([0, 0, i * 45])
        translate([sin(i*0.7)*sheet_length/4, cos(i*0.5)*sheet_width/4, 0])
            scale([sheet_length/8, 0.8, 0.4])
            rotate([90, 0, 0])
            cylinder(h=sheet_width/4, r1=sheet_thickness*2, r2=sheet_thickness*0.5);
    }
}

// Edge fraying detail
color([0.08, 0.12, 0.03])
for(i = [0:30]) {
    angle = i * 12;
    tx = cos(angle) * (sheet_length/2 + wave_amplitude/2);
    ty = sin(angle) * (sheet_width/2 + wave_amplitude/2);
    if(abs(tx) < sheet_length/2 + 2 && abs(ty) < sheet_width/2 + 2) {
        translate([tx, ty, 0])
            rotate([0, 0, angle + 90])
            scale([0.5, 1, 0.8])
            cylinder(h=sheet_thickness, r1=0.2, r2=0.5);
    }
}

// Kombu (dried kelp) strip
// 3D printable model, ~75 mm long, 25 mm wide, 2 mm thick

// Parameters
kombu_len = 75;
kombu_wid = 25;
kombu_thk = 2;
edge_wave_h = 1.5;
vein_depth = 0.3;
pore_d = 0.6;
pore_h = 0.2;
color_kombu = [0.15, 0.22, 0.10];

module kombu_strip() {
    difference() {
        // Main flat body
        color(color_kombu)
        translate([0, 0, kombu_thk/2])
        cube([kombu_len, kombu_wid, kombu_thk], center = true);

        // Taper ends slightly
        translate([kombu_len/2 + 1, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=kombu_wid*1.2, r=kombu_wid/2, center=true);
        translate([-kombu_len/2 - 1, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=kombu_wid*1.2, r=kombu_wid/2, center=true);
    }
}

module edge_waves() {
    for (i = [-kombu_len/2 + 2 : 4 : kombu_len/2 - 2]) {
        translate([i, 0, 0])
        difference() {
            color(color_kombu)
            translate([0, kombu_wid/2, kombu_thk/2])
            rotate([90, 0, 0])
            cylinder(h=edge_wave_h, r=edge_wave_h, center=true, $fn=16);
            translate([0, kombu_wid/2, -1])
            cube([5, 5, 5], center=true);
        }
        translate([i, 0, 0])
        mirror([0, 1, 0])
        difference() {
            color(color_kombu)
            translate([0, kombu_wid/2, kombu_thk/2])
            rotate([90, 0, 0])
            cylinder(h=edge_wave_h, r=edge_wave_h, center=true, $fn=16);
            translate([0, kombu_wid/2, -1])
            cube([5, 5, 5], center=true);
        }
    }
}

module central_vein() {
    color(color_kombu)
    translate([0, 0, kombu_thk - vein_depth/2])
    cube([kombu_len*0.9, kombu_wid*0.08, vein_depth], center=true);
}

module surface_pores() {
    for (x = [-kombu_len/2 + 3 : 3 : kombu_len/2 - 3]) {
        for (y = [-kombu_wid/2 + 2 : 2 : kombu_wid/2 - 2]) {
            if (y > -kombu_wid*0.15 || y < kombu_wid*0.15) {
                translate([x, y, kombu_thk - pore_h])
                cylinder(h=pore_h + 0.1, d=pore_d, $fn=8);
            }
        }
    }
}

module kombu() {
    union() {
        kombu_strip();
        edge_waves();
        central_vein();
        surface_pores();
    }
}

kombu();

// Realistic Nori Sheet with Seaweed Texture
// Medium complexity model - 50-100mm size range

module nori_sheet_base() {
    // Main nori body - thin rectangular sheet
    color([0.1, 0.15, 0.05])
    linear_extrude(height = 0.5, center = true)
        square([80, 90], center = true);
}

module nori_edge_tears() {
    // Create realistic torn edges with varying heights
    color([0.08, 0.12, 0.04])
    for(i = [0:5:360]) {
        rotate([0, 0, i])
        translate([40 + sin(i*3)*2, 0, 0.25])
            cylinder(h = 0.8, r1 = 2 + sin(i)*1, r2 = 0.5, $fn = 8);
    }

    for(i = [0:5:360]) {
        rotate([0, 0, i])
        translate([-40 - sin(i*3)*2, 0, 0.25])
            cylinder(h = 0.8, r1 = 2 + cos(i)*1, r2 = 0.5, $fn = 8);
    }
}

module seaweed_texture() {
    // Create subtle bumpy texture on surface
    color([0.12, 0.18, 0.06])
    for(x = [-35:5:35]) {
        for(y = [-40:5:40]) {
            translate([x + sin(x*y)*0.5, y + cos(x*y)*0.5, 0.75])
                sphere(d = 0.8 + sin(x/5)*0.3, $fn = 8);
        }
    }
}

module surface_lines() {
    // Add characteristic linear texture of nori
    color([0.05, 0.1, 0.03])
    for(y = [-40:2:40]) {
        rotate([90, 0, 0])
        translate([0, y, 0.5])
            cylinder(h = 80, r = 0.1, $fn = 6);
    }

    for(x = [-35:2:35]) {
        rotate([90, 0, 90])
        translate([x, 0, 0.5])
            cylinder(h = 90, r = 0.1, $fn = 6);
    }
}

module corner_folds() {
    // Add slight folding at corners for realism
    color([0.08, 0.13, 0.04])
    translate([35, 40, 0])
        rotate([5, 0, 5])
        cube([8, 8, 1]);

    translate([-35, 40, 0])
        rotate([5, 0, -5])
        cube([8, 8, 1]);

    translate([35, -40, 0])
        rotate([-5, 0, -5])
        cube([8, 8, 1]);

    translate([-35, -40, 0])
        rotate([-5, 0, 5])
        cube([8, 8, 1]);
}

module nori_complete() {
    union() {
        nori_sheet_base();
        nori_edge_tears();
        seaweed_texture();
        surface_lines();
        corner_folds();
    }
}

// Render the complete nori sheet
