// Realistic 3D printable wakame seaweed fronds
// Overall dimensions: ~70mm x 50mm x 8mm thick

// Main module to generate wakame fronds
module wakame_frond(length, width, thickness, curl_angle, wave_freq) {
    // Create base frond shape as thin ellipsoid
    scale([1, width/length, thickness/length])
    rotate([curl_angle, 0, 0])
    sphere(d=length, $fn=30);

    // Add wave ridges along frond
    for(i=[-length/2 : length/wave_freq : length/2]) {
        translate([i, 0, thickness/2])
        rotate([0, 90, 0])
        scale([0.1, 1, 0.1])
        cylinder(h=width*1.2, d=thickness*2, $fn=6);
    }

    // Add small bumps for texture
    for(i=[-length/2 : length/6 : length/2]) {
        for(j=[-width/2 : width/4 : width/2]) {
            translate([i, j, thickness/2 + 0.3])
            sphere(d=0.8, $fn=8);
        }
    }
}

// Create stem/rib structure
module wakame_stem(length, thickness) {
    color([0.1, 0.3, 0.15])
    rotate([90, 0, 0])
    cylinder(h=length, d=thickness, $fn=8);

    // Add smaller veins
    for(i=[0 : 5 : length]) {
        translate([0, 0, i])
        rotate([0, 90, 0])
        scale([0.3, 1, 0.3])
        cylinder(h=length/3, d=thickness/2, $fn=6);
    }
}

// Generate complete wakame piece
module wakame_piece() {
    // Main fronds in dark green
    color([0.1, 0.35, 0.2])
    union() {
        // Primary frond
        translate([0, 0, 2])
        rotate([0, 0, 15])
        wakame_frond(35, 20, 2, 15, 8);

        // Secondary fronds
        translate([-10, 5, 1.5])
        rotate([0, 0, -30])
        wakame_frond(25, 15, 1.5, 20, 6);

        translate([8, 8, 1.8])
        rotate([0, 0, 45])
        wakame_frond(20, 12, 1.2, 10, 5);

        translate([-5, -8, 1.2])
        rotate([0, 0, -45])
        wakame_frond(18, 10, 1, 25, 4);
    }

    // Lighter green younger fronds
    color([0.2, 0.45, 0.25])
    union() {
        translate([12, -5, 1])
        rotate([0, 0, 60])
        wakame_frond(15, 8, 0.8, 5, 3);

        translate([-15, 2, 0.8])
        rotate([0, 0, -75])
        wakame_frond(12, 6, 0.6, 8, 3);
    }

    // Central stem
    translate([0, 0, 0])
    wakame_stem(50, 3);
}

// Generate the complete model
wakame_piece();

// Dulse seaweed - a realistic 3D printable model
// Size: ~80mm long, ~40mm wide, ~3mm thick

// Main parameters
width = 40;      // Overall width
length = 80;     // Overall length
thickness = 3;   // Thickness for printing
vein_thickness = 0.6;
edge_wave = 4;

// Create a single dulse leaf
module dulse_leaf() {
    color([0.5, 0.1, 0.15])  // Deep reddish-purple color

    union() {
        // Main leaf body with organic shape
        difference() {
            // Base leaf shape
            scale([1, 1.2, 1]) 
            cube([width, length, thickness], center=true);

            // Create ruffled edges by subtracting cylinders
            for (i = [-length/2+5 : 10 : length/2-5]) {
                translate([width/2 + edge_wave/2, i, 0])
                    scale([1, 0.8, 1])
                    cylinder(h=thickness+1, r=edge_wave, $fn=20);

                translate([-width/2 - edge_wave/2, i, 0])
                    scale([1, 0.8, 1])
                    cylinder(h=thickness+1, r=edge_wave, $fn=20);
            }
        }

        // Central vein
        translate([0, 0, thickness/2 - vein_thickness/2])
            color([0.35, 0.08, 0.12])
            cube([vein_thickness*2, length-10, vein_thickness], center=true);

        // Secondary veins branching from center
        for (angle = [30, -30]) {
            rotate([0, 0, angle]) {
                for (offset = [-20, -10, 0, 10, 20]) {
                    translate([offset/3, offset, thickness/2])
                        color([0.4, 0.09, 0.13])
                        cube([vein_thickness, 15, vein_thickness/2], center=true);
                }
            }
        }

        // Small texture bumps for realistic surface
        for (x = [-width/2+5 : 5 : width/2-5]) {
            for (y = [-length/2+5 : 8 : length/2-5]) {
                translate([x + sin(y*2)*2, y, thickness/2 + 0.2])
                    scale([1, 1, 0.3])
                    sphere(r=0.5, $fn=8);
            }
        }

        // Thin edge detailing
        translate([0, 0, thickness/2])
            linear_extrude(height=0.3)
            offset(r=-1)
            offset(r=1)
            square([width-2, length-2], center=true);
    }
}

// Create a torn/damaged edge effect
module torn_edge() {
    difference() {
        dulse_leaf();

        // Random tears along edges
        for (i = [1 : 5]) {
            rotate([0, 0, i*73])
            translate([width/2-3, 0, 0])
                scale([1, 1.5, 2])
                cylinder(h=thickness+1, r=2, $fn=6, center=true);
        }
    }
}

// Main assembly
rotate([0, 0, 15])  // Natural resting angle
torn_edge();

// Arame seaweed - realistic style, medium complexity
// Size: ~80mm length

module strand_segment(radius, length, twist_angle) {
    // Individual strand segment with slight twist
    rotate([0, 0, twist_angle])
        cylinder(h=length, r=radius, $fn=8);
}

module arame_strand(length, thickness, segments) {
    // Create one arame strand with natural curves
    for(i=[0:segments-1]) {
        segment_length = length / segments;
        twist = sin(i*15) * 5; // Natural twisting
        offset_x = sin(i*0.3) * 3; // Gentle wave
        offset_y = cos(i*0.2) * 2;

        translate([offset_x, offset_y, i*segment_length])
            rotate([sin(i*0.1)*10, cos(i*0.15)*8, twist])
                strand_segment(thickness, segment_length, i*2);
    }
}

module texture_detail(pos_x, pos_y, pos_z) {
    // Small bumps for seaweed texture
    translate([pos_x, pos_y, pos_z])
        scale([1, 1, 0.3])
            sphere(r=0.4, $fn=6);
}

module arame_bundle(strands, base_radius) {
    // Create a natural bundle of arame strands
    union() {
        for(i=[0:strands-1]) {
            angle = i * (360/strands) + (rand(i)*10-5);
            radius_offset = base_radius * (0.3 + rand(i+100)*0.7);

            rotate([0, 0, angle])
                translate([radius_offset, 0, 0])
                    rotate([90 + rand(i+50)*20-10, 0, 0])
                        arame_strand(
                            40 + rand(i+200)*40,  // length 40-80mm
                            0.6 + rand(i+300)*0.4, // thickness
                            8 + floor(rand(i+400)*4)  // segments
                        );
        }
    }
}

// Random helper function for natural variation
function rand(n) = (sin(n*12.9898 + 78.233) * 43758.5453) % 1;

// Main arame model
color([0.15, 0.35, 0.25])  // Dark green-brown seaweed color
difference() {
    union() {
        // Main bundle
        arame_bundle(12, 15);

        // Additional loose strands
        translate([10, 5, 0])
            rotate([0, 0, 30])
                arame_strand(30, 0.8, 6);

        translate([-8, -3, 5])
            rotate([0, 0, -45])
                arame_strand(35, 0.7, 7);

        // Root/base cluster
        translate([0, 0, -2])
            cylinder(h=4, r=8, $fn=12);
    }

    // Remove bottom flat for printing
    translate([0, 0, -3])
        cube([200, 200, 3], center=true);
}

// Add texture details on some strands
color([0.2, 0.4, 0.3])  // Slightly lighter texture color
for(i=[0:30]) {
    translate([
        rand(i*100)*80-40,
        rand(i*200)*80-40,
        rand(i*300)*70
    ])
        texture_detail(0, 0, 0);
}

// Hijiki seaweed - realistic medium complexity model

module hijiki_strand(length, thickness, curl) {
    // Create a curled strand of hijiki
    for(i = [0:5:length]) {
        translate([sin(i * curl) * 3, cos(i * curl) * 3, i])
            rotate([0, 0, i * curl * 10])
                scale([1 + sin(i * 0.5) * 0.2, 1 + cos(i * 0.5) * 0.2, 1])
                    cylinder(h = 2, r = thickness, $fn = 8);
    }
}

module hijiki_branch() {
    // Main stem
    color([0.1, 0.1, 0.05])
    union() {
        // Central stem
        rotate([90, 0, 0])
            cylinder(h = 25, r1 = 1.5, r2 = 2, $fn = 6);

        // Side branches
        for(i = [0:3]) {
            rotate([90, 0, i * 120])
                translate([0, 0, 5 + i * 6])
                    rotate([0, -20 - i * 5, 0])
                        cylinder(h = 8 - i, r1 = 1, r2 = 0.5, $fn = 6);
        }

        // Branch tips with curled strands
        for(i = [0:3]) {
            rotate([90, 0, i * 120])
                translate([0, 0, 12 + i * 6])
                    hijiki_strand(8 - i * 1.5, 0.3, 0.3);
        }
    }
}

module hijiki_texture() {
    // Create surface texture with small bumps
    for(i = [0:360:30]) {
        rotate([0, 0, i])
            for(j = [0:2:8]) {
                translate([j * 0.5, 0, j])
                    rotate([90, 0, 0])
                        sphere(0.2, $fn = 6);
            }
    }
}

module hijiki_main() {
    color([0.08, 0.08, 0.03])
    union() {
        // Base cluster
        for(i = [0:5]) {
            rotate([0, 0, i * 72])
                translate([sin(i * 72) * 8, cos(i * 72) * 8, 0])
                    hijiki_branch();
        }

        // Additional small pieces
        for(i = [0:8]) {
            rotate([0, 0, i * 45])
                translate([sin(i * 45) * 15, cos(i * 45) * 15, 0])
                    scale([0.5, 0.5, 0.7])
                        hijiki_branch();
        }

        // Central root piece
        translate([0, 0, -2])
            scale([1.2, 1.2, 0.5])
                cylinder(h = 4, r1 = 5, r2 = 3, $fn = 8);
    }
}

// Assembly
hijiki_main();

// Add some loose strands on top
for(i = [0:3]) {
    rotate([0, 0, i * 90])
        translate([sin(i * 90) * 10, cos(i * 90) * 10, 2])
            hijiki_strand(12, 0.4, 0.4);
}

// Agar Dessert with Fruit Cubes
// Realistic style, medium complexity

$fn = 32;

// Main agar block dimensions
block_width = 60;
block_height = 45;
block_depth = 60;

// Wall thickness for 3D printing
wall_thickness = 2;

// Fruit cube dimensions
fruit_size = 6;
fruit_count = 12;

// Colors
agar_color = [0.9, 0.95, 0.8, 0.7];
red_color = [0.9, 0.3, 0.3, 0.9];
orange_color = [0.9, 0.6, 0.2, 0.9];
green_color = [0.3, 0.7, 0.3, 0.9];

// Main agar block with rounded edges
module agar_block() {
    difference() {
        // Outer rounded block
        minkowski() {
            cube([block_width, block_height, block_depth], center=true);
            sphere(r=3);
        }

        // Hollow center for material saving
        translate([0, 0, 0])
            cube([block_width - wall_thickness*2, block_height - wall_thickness*2, block_depth - wall_thickness*2], center=true);
    }
}

// Create a single fruit cube with beveled edges
module fruit_cube(color) {
    color(color)
    difference() {
        // Main cube
        cube([fruit_size, fruit_size, fruit_size], center=true);

        // Bevel edges slightly
        for(i=[-1,1])
        for(j=[-1,1])
        for(k=[-1,1])
            translate([i*(fruit_size/2-1), j*(fruit_size/2-1), k*(fruit_size/2-1)])
                sphere(r=1.2);
    }
}

// Generate random positions for fruit cubes
module fruit_positions() {
    // Pre-calculated positions for consistent results
    positions = [
        [-20, -15, -20], [0, -10, -15], [20, -12, -18],
        [-15, 0, 0], [15, 5, 5], [-5, 8, -10],
        [10, -5, 15], [-20, 10, 10], [25, 0, -5],
        [0, 15, 20], [-10, -8, 5], [18, 12, 0]
    ];

    colors = [red_color, orange_color, green_color];

    for(i = [0:fruit_count-1]) {
        translate(positions[i]) {
            fruit_cube(colors[i % 3]);
        }
    }
}

// Surface texture bubbles
module surface_bubbles() {
    color(agar_color)
    for(i = [1:20]) {
        theta = i * 137.5; // Golden angle
        radius = 5 + (i * 2) % 15;
        x = cos(theta) * radius;
        z = sin(theta) * radius;
        y = block_height/2 + 0.5;

        translate([x, y, z])
            scale([1, 0.5, 1])
                sphere(r=0.5 + (i % 3) * 0.3);
    }
}

// Assemble the complete agar dessert
union() {
    // Main agar block
    color(agar_color)
    agar_block();

    // Fruit cubes inside
    fruit_positions();

    // Surface bubbles for texture
    surface_bubbles();

    // Top surface slight waviness
    difference() {
        color(agar_color)
        translate([0, 0, 0])
            cube([block_width + 2, 2, block_depth + 2], center=true);

        // Create gentle waves
        for(i = [-2:2]) {
            for(j = [-2:2]) {
                translate([i*12, 2, j*12])
                    sphere(r=3 + (abs(i) + abs(j)) * 0.5);
            }
        }
    }
}

