// Soybean pod module
module soybean_pod() {
    // Pod shell - hollow with rounded edges
    difference() {
        // Outer shell
        hull() {
            // Main body
            scale([1, 0.6, 0.4])
                cylinder(h=pod_length, d=pod_width);

            // Rounded ends
            translate([0, 0, pod_length])
                scale([1, 0.6, 0.4])
                    sphere(d=pod_width);
            translate([0, 0, 0])
                scale([1, 0.6, 0.4])
                    sphere(d=pod_width);
        }

        // Hollow interior
        hull() {
            // Inner cavity
            scale([1, 0.65, 0.45])
                cylinder(h=pod_length - 2, d=pod_width - 2*wall_thickness);

            // Rounded inner ends
            translate([0, 0, pod_length - 1])
                scale([1, 0.65, 0.45])
                    sphere(d=pod_width - 2*wall_thickness);
            translate([0, 0, 1])
                scale([1, 0.65, 0.45])
                    sphere(d=pod_width - 2*wall_thickness);
        }
    }

    // Pod seam detail
    translate([0, pod_width*0.3, pod_length/2])
        rotate([90, 0, 0])
            cylinder(h=pod_length, d=wall_thickness*0.5, $fn=8);

    // Surface texture - subtle bumps
    for (i = [1:10]) {
        for (j = [1:4]) {
            rotate([0, 0, i*36])
                translate([pod_width*0.25, 0, j*pod_length/5])
                    scale([0.8, 0.6, 0.3])
                        sphere(d=2);
        }
    }
}

// Individual soybean bean module
module soybean_bean() {
    color("#C4A484")
    scale([1, 0.7, 0.5])
        sphere(d=bean_diameter);

    // Bean hilum (scar)
    color("#8B7355")
    translate([0, bean_diameter*0.3, 0])
        rotate([90, 0, 0])
            cylinder(h=0.5, d=1.5);
}

// Complete soybean with pod and beans
module complete_soybean() {
    // Pod in realistic green-brown color
    color("#8FBC8F")
    soybean_pod();

    // Beans inside pod
    for (i = [1:beans_per_pod]) {
        translate([0, pod_width*0.2, i*pod_length/(beans_per_pod+1)])
            rotate([0, 90, 0])
                soybean_bean();
    }
}

// Create the model
rotate([90, 0, 0])
    complete_soybean();

// Black bean - realistic style, medium complexity
// Approx 15mm long, 8mm wide, 6mm thick

color([0.15, 0.08, 0.05]) // Dark brown-black color

module black_bean() {
    difference() {
        // Main bean body - slightly flattened ellipsoid
        scale([1, 0.7, 0.45]) sphere(d=10, $fn=50);

        // Central seam/groove
        translate([0, 0, 4.4])
            rotate([90, 0, 0])
            linear_extrude(height=10, center=true)
                polygon(points=[[-5,0], [5,0], [0,-0.5]]);
    }

    // Add slight dimple at one end
    translate([4.8, 0, 0])
        rotate([0, 90, 0])
        scale([1, 1, 0.8])
        sphere(d=1.5, $fn=20);

    // Add slight dimple at other end
    translate([-4.8, 0, 0])
        rotate([0, 90, 0])
        scale([1, 1, 0.8])
        sphere(d=1.5, $fn=20);

    // Surface texture - small imperfections
    for(i=[-4:4]) {
        for(j=[-2:2]) {
            if((i+j)%2==0) {
                translate([i*1.2, j*1.2, 2.3+sin(i*45)*0.2])
                    sphere(d=0.3, $fn=8);
            }
        }
    }
}

