// Pinto Bean - Realistic Style
// Medium complexity 3D printable model

color([0.85, 0.65, 0.45])  // Natural pinto bean color

module pinto_bean_body() {
    // Main bean body - elliptical shape
    difference() {
        scale([1.2, 0.7, 0.4])
            sphere(d=50, $fn=50);

        // Flatten bottom slightly for stable printing
        translate([0, 0, -10])
            cube([100, 100, 20], center=true);
    }
}

module bean_dent() {
    // Central dent along bean
    rotate([90, 0, 0])
        translate([0, 0, -5])
            scale([0.05, 1, 0.1])
                cylinder(h=30, d=40, $fn=20);
}

module brown_spots() {
    // Characteristic brown spots on pinto beans
    for(i = [0:7]) {
        rotate([0, 0, i*45])
            translate([12 + (i%3)*3, 0, 2 + sin(i*30)*2])
                scale([1, 0.7, 0.3])
                    sphere(d=6, $fn=8);
    }

    for(i = [0:5]) {
        rotate([0, 0, i*60 + 30])
            translate([-10 + cos(i*25)*2, 0, -1 + cos(i*40)*1.5])
                scale([0.8, 0.6, 0.2])
                    sphere(d=4, $fn=6);
    }
}

module surface_texture() {
    // Subtle bump texture for realistic feel
    for(x = [-20:5:20]) {
        for(y = [-12:4:12]) {
            if(x*x/400 + y*y/144 < 0.9) {
                translate([x, y, 0])
                    rotate([90, 0, 0])
                        cylinder(h=0.5, d=1.5, $fn=6);
            }
        }
    }
}

module bean_tip() {
    // Slight point at one end
    translate([22, 0, 0])
        rotate([0, 90, 0])
            scale([0.3, 0.3, 0.2])
                cylinder(h=8, d1=10, d2=4, $fn=10);
}

union() {
    // Main bean body
    pinto_bean_body();

    // Central dent
    difference() {
        color([0.9, 0.7, 0.5]) children();
        bean_dent();
    }

    // Brown spots
    color([0.6, 0.4, 0.25]) brown_spots();

    // Surface texture
    color([0.87, 0.67, 0.47]) surface_texture();

    // Pointed tip
    color([0.85, 0.65, 0.45]) bean_tip();
}

