// Cannellini Bean Model
// Realistic medium-complexity 3D printable bean

// Parameters
bean_length = 25;
bean_width = 12;
bean_height = 8;
wall_thickness = 1.2;
seed_count = 8;

// Main bean body module
module bean_body() {
    color([0.95, 0.9, 0.82]) {
        // Central bean shape using ellipsoid
        scale([1, 0.7, 0.5]) sphere(d = bean_length);

        // Add subtle ridge along top
        translate([0, 0, bean_height/2 - 0.5])
            scale([1, 0.1, 0.05])
                sphere(d = bean_length * 0.9);
    }
}

// Bean seam detail
module bean_seam() {
    color([0.85, 0.8, 0.72]) {
        // Subtle indentation along center
        translate([0, 0, -1])
            scale([1, 0.02, 0.3])
                cube([bean_length * 0.9, bean_width * 0.8, bean_height], center = true);
    }
}

// Bean tip detail
module bean_tip() {
    color([0.9, 0.85, 0.77]) {
        // Slight point at ends
        translate([bean_length/2 - 1, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = 2, r1 = 2.5, r2 = 0);

        translate([-bean_length/2 + 1, 0, 0])
            rotate([0, -90, 0])
                cylinder(h = 2, r1 = 2.5, r2 = 0);
    }
}

// Surface texture - slight dimples
module bean_texture() {
    color([0.92, 0.87, 0.79]) {
        for(i = [1:seed_count]) {
            rotate([0, 0, i * 45]) {
                translate([bean_length/3 * sin(i*20), bean_width/3 * cos(i*20), -1])
                    sphere(d = 1.5);
                translate([-bean_length/3 * sin(i*20), -bean_width/3 * cos(i*20), -1])
                    sphere(d = 1.5);
            }
        }
    }
}

// Hollowing for 3D printing
module hollow_bean() {
    difference() {
        union() {
            bean_body();
            bean_tip();
        }

        // Internal hollow space
        scale([0.8, 0.6, 0.4])
            sphere(d = bean_length);

        // Drainage hole at bottom
        translate([0, 0, -bean_height/2 - 1])
            cylinder(h = 2, r = 2);
    }
}

// Assembly
union() {
    // Main bean structure
    difference() {
        hollow_bean();
        bean_seam();
    }

    // Surface details
    translate([0, 0, bean_height/2 - 1])
        bean_texture();
}

// Realistic Lima Bean
// Medium complexity, 3D printable

// Parameters
bean_length = 45;
bean_width = 30;
bean_thickness = 12;
wall_thickness = 1.5;
seed_depth = 1.5;

// Colors
lima_color = [0.85, 0.78, 0.65];
hilum_color = [0.4, 0.35, 0.3];

// Main bean body
module bean_body() {
    difference() {
        // Main ellipsoid shape
        scale([1, 0.7, 0.4])
        sphere(d = bean_length, $fn = 64);

        // Flatten the bottom slightly
        translate([0, 0, -bean_length * 0.2])
        cube([bean_length * 1.5, bean_length * 1.5, bean_length * 0.2], center = true);
    }
}

// Create the bean seam
module bean_seam() {
    // Central ridge along the length
    translate([0, 0, bean_thickness * 0.15])
    scale([1.05, 0.1, 0.15])
    sphere(d = bean_length * 0.95, $fn = 64);
}

// Hilum (seed attachment point)
module hilum() {
    color(hilum_color)
    translate([-bean_length * 0.45, 0, bean_thickness * 0.25])
    rotate([0, 90, 0])
    cylinder(h = 2, d1 = 3, d2 = 1.5, $fn = 16);
}

// Surface texture pattern
module surface_texture() {
    // Small indentations for texture
    for (i = [-4:4]) {
        for (j = [-2:2]) {
            if (i*i + j*j < 20) {
                translate([i * 4, j * 4, bean_thickness * 0.5])
                sphere(d = seed_depth * 2, $fn = 8);
            }
        }
    }
}

// Create the hollow interior
module hollow_interior() {
    scale([0.85, 0.6, 0.3])
    sphere(d = bean_length * 0.9, $fn = 64);
}

// Generate the complete lima bean
color(lima_color)
difference() {
    union() {
        // Main body
        bean_body();

        // Seam
        bean_seam();

        // Hilum
        hilum();
    }

    // Hollow out interior
    translate([0, 0, -bean_thickness * 0.1])
    hollow_interior();

    // Surface texture (subtract spheres)
    surface_texture();
}

