// Cranberry bean - realistic style, medium complexity
// Dimensions: ~65mm length, ~15mm width

// Main color for cranberry bean
color([0.7, 0.3, 0.25]) {

    // Main bean body - slightly curved ellipsoid
    difference() {
        // Base bean shape
        union() {
            // Main ellipsoid body
            scale([1, 0.7, 0.4])
                sphere(10);

            // Tapered ends
            translate([-10, 0, 0])
                scale([0.8, 0.6, 0.3])
                    sphere(10);
            translate([10, 0, 0])
                scale([0.8, 0.6, 0.3])
                    sphere(10);
        }

        // Hollow interior for 3D printing
        scale([0.8, 0.5, 0.3])
            sphere(10);
    }

    // Characteristic cranberry bean markings
    module bean_marking(x, y, z, angle) {
        translate([x, y, z]) {
            rotate([0, 0, angle]) {
                // Darker red-brown stripe
                color([0.5, 0.2, 0.15]) {
                    scale([8, 1, 0.3])
                        sphere(1.2);
                }
            }
        }
    }

    // Add multiple markings across the bean
    for (i = [-2:1:2]) {
        for (j = [-1:1:1]) {
            bean_marking(
                i * 8,
                j * 3 + sin(i * 45) * 2,
                cos(i * 30) * 2,
                i * 15
            );
        }
    }

    // Bean seam ridge
    color([0.6, 0.25, 0.2]) {
        translate([0, 0, 3.5])
            scale([20, 1.5, 0.5])
                sphere(1);
    }

    // Seed texture pattern
    module seed_texture() {
        for (i = [0:15:180]) {
            rotate([0, i, 0]) {
                translate([0, 0, 3]) {
                    scale([0.5, 0.5, 0.2])
                        sphere(3);
                }
            }
        }
    }

    // Apply texture to both sides
    translate([0, 0, 3.8])
        scale([0.9, 0.9, 1])
            seed_texture();

    translate([0, 0, -3.8])
        scale([0.9, 0.9, 1])
            rotate([0, 180, 0])
                seed_texture();

    // Small indentation at bean tip (hilum)
    color([0.4, 0.15, 0.1]) {
        translate([-19, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=2, r1=1.5, r2=0.5);
    }
}

