        translate([0, 0, 0])
            scale([1, side, 1])
                cube([pod_length, pod_width, pod_thickness], center=true);

        // Hollow inside
        translate([0, 0, 0])
            scale([0.9, side*0.8, 0.8])
                cube([pod_length + 2, pod_width - 1, pod_thickness + 2], center=true);

