            bean_pos = -pod_length/2 + 15 + i * 15;
            translate([bean_pos, -side*3, 0])
                scale([1.2, 0.8, 0.9])
                    rotate([0, 90, 0])
                        cylinder(h=bean_length + 2, r=bean_width/2 + 1, $fn=20);
        }
    }

    // Pod seam
    color("#5D7C47")
    translate([0, 0, pod_thickness/2 - 0.5])
        cube([pod_length, 1.5, 1], center=true);

    // Texture on outer surface
    pod_texture();
}

