// Create the bean pod
module lupin_pod() {
    color(pod_color)
    difference() {
        // Main pod body - elongated ellipsoid
        scale([1, 0.6, 0.3])
        sphere(r=pod_width/2);

        // Flatten the bottom
        translate([0, 0, -pod_width/2])
        cube([pod_width*2, pod_width*2, pod_width], center=true);

        // Create seam line
        translate([0, 0, -pod_thickness/2])
        cube([pod_length, 1, pod_thickness], center=true);

        // Create pod opening
        translate([pod_length/4, 0, 0])
        scale([1, 0.8, 0.5])
        sphere(r=pod_width/2);
    }

    // Add slight texture lines along pod
    for(i = [-1:0.2:1]) {
        translate([i * pod_length/3, 0, pod_thickness/2])
        rotate([0, 90, 0])
        scale([0.1, 0.1, pod_length/2])
        cylinder(h=1, r=0.5);
    }
