// Individual lupin bean
module lupin_bean() {
    color(bean_color)
    difference() {
        // Main bean shape - flattened sphere
        scale([1, 0.7, 0.4])
        sphere(r=bean_size);

        // Add characteristic dent
        translate([0, -bean_size/2, 0])
        scale([1.2, 0.5, 0.5])
        sphere(r=bean_size/2);
    }

    // Bean hilum (attachment scar)
    translate([0, 0, -bean_size/2 + 0.5])
    rotate([90, 0, 0])
    cylinder(h=0.3, r=1.5);
}

// Create the full lupin with beans
