// Module for creating cowpea pod curve
module cowpea_curve(t) {
    // Parametric curve for natural bean pod shape
    x = t * cowpea_length;
    y = sin(t * 3.14159 * 2) * cowpea_width/4;
    z = cos(t * 3.14159) * cowpea_thickness/8;
    [x, y, z];
}

// Create the main pod body
module pod_body() {
    hull() {
        for (i = [0:0.1:1]) {
            translate(cowpea_curve(i))
                sphere(d = cowpea_width - (i * 2), $fn = 16);
        }
    }
}

// Create pod shell with hollow interior
module pod_shell() {
    difference() {
        // Outer shell
        color(cowpea_color)
        pod_body();

        // Inner cavity
        scale([0.85, 0.85, 0.85])
            translate([5, 0, 0])
                pod_body();
    }
}

// Individual cowpea seed
module cowpea_seed() {
    color(seed_color)
    difference() {
        // Main seed body
        scale([1, 0.7, 0.4])
            sphere(d = 6, $fn = 20);

        // Seed dimple
        translate([2, 0, 0])
            scale([1, 0.8, 0.8])
                sphere(d = 2, $fn = 10);

        // Hilum (scar)
        translate([-2.8, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = 1.5, d1 = 1.2, d2 = 0.6, $fn = 8);
    }
}

