    color("#654321")
    for (i = [0:5:cowpea_length-10]) {
        translate([i, 0, cowpea_thickness/2 + 0.2])
            rotate([90, 0, 0])
                cylinder(h = 0.5, d1 = 1.5, d2 = 1, $fn = 6);
    }
}

// Create pod texture
module pod_texture() {
    color("#A0522D")
    for (i = [10:10:cowpea_length-10]) {
        for (j = [-1, 1]) {
            translate([i, j * cowpea_width/3, cowpea_thickness/2 + 0.1])
                rotate([90, 0, 0])
                    cylinder(h = 0.3, d = 2, $fn = 8);
        }
    }
}

// Place seeds inside pod
module place_seeds() {
    for (i = [0:seed_count-1]) {
        translate([10 + i * 8, 0, -cowpea_thickness/4])
            rotate([0, 90, 0])
