// Hyacinth Food Model - Realistic Style, Medium Complexity
// A 3D printable model of a hyacinth bulb with roots and sprouting shoots

// Main colors for hyacinth
color_bulb = "#F5F5DC";  // Beige for bulb
color_roots = "#DEB887"; // Burlywood for roots
color_sprout = "#90EE90"; // Light green for shoots
color_skin = "#8B7355";  // Darker brown for outer skin

// Bulb dimensions
bulb_height = 40;
bulb_radius = 25;
wall_thickness = 2;

// Root parameters
root_count = 8;
root_length = 30;
root_radius = 1.5;

// Sprout parameters
sprout_count = 6;
sprout_height = 25;
sprout_radius = 3;

// Function to create tapered roots
module tapered_root(length, base_radius) {
    for (i = [0:3:length]) {
        translate([0, 0, -i]) {
            cylinder(h=3, r1=base_radius * (1 - i/length), r2=base_radius * (1 - (i+3)/length), $fn=8);
        }
    }
}

// Create outer bulb skin with papery texture
module bulb_skin() {
    color(color_skin) {
        difference() {
            // Main bulb shape
            scale([1, 1, 0.8]) sphere(r=bulb_radius, $fn=32);

            // Hollow inside
            translate([0, 0, -5]) 
                scale([1, 1, 0.75]) 
                    sphere(r=bulb_radius - wall_thickness, $fn=32);

            // Flat bottom
            translate([0, 0, -bulb_height/2]) 
                cube([bulb_radius*2, bulb_radius*2, bulb_height], center=true);
        }
    }
}

// Create inner bulb flesh
module bulb_flesh() {
    color(color_bulb) {
        translate([0, 0, -5]) {
            scale([1, 1, 0.75]) 
                sphere(r=bulb_radius - wall_thickness, $fn=32);

            // Add growth rings texture
            for (i = [1:3:bulb_radius-5]) {
                translate([0, 0, -i/4]) 
                    scale([1, 1, 0.1]) 
                        sphere(r=i, $fn=32);
            }
        }
    }
}

// Create root system
module roots() {
    color(color_roots) {
        for (i = [0:root_count-1]) {
            angle = i * 360 / root_count;
            translate([cos(angle) * (bulb_radius - 5), sin(angle) * (bulb_radius - 5), -bulb_height/2]) {
                rotate([90, 0, angle + 15]) {
                    tapered_root(root_length, root_radius);
                }
            }
        }
    }
}

// Create sprouting shoots
module shoots() {
    color(color_sprout) {
        for (i = [0:sprout_count-1]) {
            angle = i * 360 / sprout_count;
            translate([cos(angle) * 10, sin(angle) * 10, bulb_height/2 - 5]) {
                // Curved shoot
                rotate_extrude(angle=90, $fn=16) {
                    translate([0, 0, 0]) {
                        for (t = [0:2:sprout_height]) {
                            translate([0, t, 0]) {
                                circle(r=sprout_radius * (1 - t/sprout_height/2));
                            }
                        }
                    }
                }

                // Shoot tip
                translate([cos(angle) * 5, sin(angle) * 5, sprout_height - 5]) {
                    sphere(r=sprout_radius * 0.8, $fn=8);
                }
            }
        }
    }
}

// Base plate for printing stability
module base() {
    color("#8B4513") {
        translate([0, 0, -bulb_height/2 - 5]) {
            cylinder(h=3, r=bulb_radius + 5, $fn=32);
        }
    }
}

// Assembly
union() {
    base();
    bulb_skin();
    bulb_flesh();
    roots();
    shoots();
}

