gap = 0.5;
wall_thickness = 1.2;

// Main module
module sugar_cube(x, y, z, rotation) {
    translate([x, y, z]) {
        rotate([0, 0, rotation]) {
            // Main cube body with slight irregularity
            color([0.95, 0.95, 0.92]) {
                // Slightly irregular cube shape
                for(i = [0:3]) {
                    rotate([0, 0, i * 90]) {
                        translate([sin(i * 45) * 0.3, 0, 0]) {
                            cube([cube_size + sin(i * 30) * 0.4, 
                                  cube_size + cos(i * 45) * 0.3, 
                                  cube_size + sin(i * 60) * 0.2], true);
                        }
                    }
                }

                // Sugar crystal texture on top surface
                for(tx = [-3.5:0.8:3.5]) {
                    for(ty = [-3.5:0.8:3.5]) {
                        translate([tx, ty, cube_size/2 + 0.1]) {
                            scale([1, 1, 0.3]) {
                                sphere(r = 0.2 + rand(0.1), $fn = 6);
                            }
                        }
                    }
                }

                // Side texture - small irregularities
                for(side = [0:3]) {
                    rotate([0, 0, side * 90]) {
                        translate([cube_size/2 + 0.1, 0, 0]) {
                            rotate([0, 90, 0]) {
                                for(sz = [-3:1:3]) {
                                    for(sy = [-3:0.5:3]) {
                                        translate([sz, sy, 0]) {
                                            sphere(r = 0.1 + rand(0.05), $fn = 4);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }

                // Rounded edges for realistic wear
                for(edge = [0:4]) {
                    rotate([0, 0, edge * 72]) {
                        translate([(cube_size/2 - 0.5) * cos(edge * 72), 
                                  (cube_size/2 - 0.5) * sin(edge * 72), 
                                  cube_size/2 - 0.5]) {
                            rotate([45, 45, 0]) {
                                cylinder(h = 0.5, r = 0.3, $fn = 8);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Random number generator for irregularities
function rand(seed) = (abs(sin(seed * 123.456)) * 0.4);

