            cylinder(h=2, r=0.5, $fn=8);
        }
    }
}

// Main cranberry body with surface texture
module cranberry_body() {
    color(color_main) {
        difference() {
            // Base ellipsoid shape
            scale([1, 1.1, 0.9]) {
                sphere(r=cranberry_radius, $fn=64);
            }

            // Add surface bumps
            union() {
                for(i = [0:bump_count-1]) {
                    angle1 = i * 360 / bump_count;
                    angle2 = (i * 137.5) % 360; // Golden angle for natural distribution

                    // Calculate position on sphere surface
                    x = cos(angle1) * sin(angle2) * (cranberry_radius - 1);
                    y = sin(angle1) * sin(angle2) * (cranberry_radius - 1);
                    z = cos(angle2) * (cranberry_radius - 1);

                    translate([x, y, z]) {
                        rotate([0, 0, angle1]) {
                            surface_bump(2.5 + sin(i*3)*0.5, bump_height);
                        }
                    }
                }
            }
        }
    }
}

// Add subtle highlights for realism
module highlights() {
    color(color_highlight, 0.3) {
        // Top highlight
        translate([0, -cranberry_radius/3, cranberry_radius/2]) {
            scale([0.6, 0.8, 0.3]) {
                sphere(r=cranberry_radius/2, $fn=32);
            }
        }

        // Side highlight
        translate([cranberry_radius/2, 0, 0]) {
            scale([0.3, 0.4, 0.5]) {
                sphere(r=cranberry_radius/2, $fn=32);
            }
        }
    }
}

// Combine all elements
union() {
    // Main cranberry body
    cranberry_body();

    // Add stem scar on top
    translate([0, 0, 0]) {
        stem_scar(stem_scar_depth);
    }

    // Add subtle highlights
    highlights();

    // Slight rotation for natural appearance
    rotate([5, -8, 12]) {
        children();
    }
}

