// Butter stick with wrapper partially peeled back
// Realistic style, medium complexity

// Main dimensions (in mm)
butter_width = 25;
butter_length = 90;
butter_height = 20;
wrapper_thickness = 0.5;
peel_back = 30;

// Colors
butter_color = [1, 0.9, 0.6];
wrapper_color = [0.9, 0.85, 0.7];
wrapper_text_color = [0.7, 0.65, 0.6];

// Butter stick body module
module butter_body() {
    // Main butter block
    color(butter_color) {
        difference() {
            // Main block
            translate([-butter_width/2, 0, 0])
                cube([butter_width, butter_length, butter_height]);

            // Top surface texture - small indentations
            for (x = [-butter_width/2+2 : 4 : butter_width/2-2]) {
                for (y = [5 : 5 : butter_length-5]) {
                    translate([x, y, butter_height-0.2])
                        sphere(d=1.2);
                }
            }

            // Side texture marks
            for (i = [0 : 10 : butter_length-10]) {
                translate([-butter_width/2-0.1, i+5, 5])
                    rotate([0, 90, 0])
                        cylinder(h=butter_width+0.2, r=0.5, $fn=8);
            }
        }
    }
}

// Wrapper module
module wrapper() {
    color(wrapper_color) {
        // Bottom wrapper
        translate([-butter_width/2-wrapper_thickness, -wrapper_thickness, -wrapper_thickness])
            cube([butter_width+2*wrapper_thickness, butter_length+wrapper_thickness, wrapper_thickness]);

        // Side wrapper pieces
        translate([-butter_width/2-wrapper_thickness, -wrapper_thickness, 0])
            cube([wrapper_thickness, butter_length+wrapper_thickness, butter_height]);

        translate([butter_width/2, -wrapper_thickness, 0])
            cube([wrapper_thickness, butter_length+wrapper_thickness, butter_height]);

        // End wrapper
        translate([-butter_width/2-wrapper_thickness, butter_length, 0])
            cube([butter_width+2*wrapper_thickness, wrapper_thickness, butter_height]);

        // Top wrapper (partially peeled back)
        difference() {
            // Full top wrapper
            translate([-butter_width/2-wrapper_thickness, -wrapper_thickness, butter_height])
                cube([butter_width+2*wrapper_thickness, butter_length+wrapper_thickness, wrapper_thickness]);

            // Peeled back portion
            translate([-butter_width/2-wrapper_thickness-1, butter_length-peel_back, -1])
                cube([butter_width+2*wrapper_thickness+2, peel_back+2, butter_height+2]);
        }

        // Wrapper flap that peels back
        translate([0, butter_length-peel_back, butter_height+wrapper_thickness])
            rotate([80, 0, 0])
                cube([butter_width+2*wrapper_thickness, peel_back, wrapper_thickness]);
    }

    // Print markings on wrapper
    color(wrapper_text_color) {
        for (y = [10 : 15 : butter_length-20]) {
            translate([0, y, butter_height+wrapper_thickness+0.1])
                cube([butter_width*0.8, 0.5, 0.1]);
        }

        // Measurement markings
        for (i = [1 : 4]) {
            translate([butter_width/2+wrapper_thickness+0.1, i*butter_length/5, butter_height/2])
                cube([2, 0.5, 3]);
        }
    }
}

// Assembly
union() {
    butter_body();
    wrapper();
}

