            cylinder(h = cookie_t + 0.2, d = cookie_d - wall*2, $fn = 64); // hollow underside
    }
}

// Irregular edge bumps for realism
module edge_bumps() {
    for(i=[0:23]) {
        angle = i*15 + (i%2)*7.5;
        r = cookie_d/2 + (i%3)*2;
        translate([r*cos(angle), r*sin(angle), cookie_t/2])
            rotate([0,0,angle])
            scale([1.3,1,1])
            cylinder(h = cookie_t, d = 4 + (i%2)*2, $fn = 8);
    }
}

// Small random seeds on surface
module seeds() {
    for(i=[0:30]) {
        r = cookie_d/2 - 5;
        x = rands(-r, r, 1, seed+i)[0];
        y = rands(-r, r, 1, seed+i+100)[0];
        if(norm([x,y]) < r) {
            translate([x, y, cookie_t])
                scale([1,1,0.5])
                sphere(d = 1.2, $fn=6);
        }
    }
}

// Appaloosa-style dark spots
module spots() {
    for(i=[0:spot_count-1]) {
        angle = i*360/spot_count + rands(-15,15,1,seed+i)[0];
        r = rands(cookie_d*0.15, cookie_d*0.4, 1, seed+i+200)[0];
        x = r*cos(angle);
        y = r*sin(angle);
        size = spot_d + rands(-1.5,1.5,1,seed+i+300)[0];
        translate([x, y, cookie_t])
            cylinder(h = 0.6, d = size, $fn = 20);
    }
}

