// Overall cracker module
module cracker() {
    // Base cracker body
    color([0.95, 0.92, 0.85]) // Off-white cream color

    difference() {
        // Main body with rounded edges
        union() {
            // Central thick body
            translate([0, 0, 2.5])
                cube([50, 50, 5], center=true);

            // Rounded top edges
            for(i = [-20, 20]) {
                translate([i, 0, 4.5])
                    rotate([90, 0, 0])
                        cylinder(h=25, r=2.5, $fn=20);
                translate([0, i, 4.5])
                    rotate([0, 90, 0])
                        cylinder(h=25, r=2.5, $fn=20);
            }
        }

        // Corner rounding cuts
        for(x = [-20, 20])
            for(y = [-20, 20])
                translate([x, y, 0])
                    cylinder(h=10, r=2.5, center=true, $fn=20);
    }

    // Docking holes (air pockets)
    color([0.9, 0.87, 0.8])
    for(x = [-30, -15, 0, 15, 30])
        for(y = [-30, -15, 0, 15, 30])
            translate([x, y, 2])
                cylinder(h=4, r=1.5, $fn=12);

    // Surface texture - raised dots
    color([0.97, 0.94, 0.88])
    for(x = [-35:5:35])
        for(y = [-35:5:35])
            if(abs(x) < 23 && abs(y) < 23)
                translate([x, y, 5.1])
                    sphere(r=0.4, $fn=6);

    // Edge perforation pattern
    color([0.92, 0.89, 0.82])
    // Top edge
    for(x = [-22.5:2.5:22.5]) {
        translate([x, 24.5, 2.5])
            cube([1.5, 1, 5], center=true);
        translate([x, -24.5, 2.5])
            cube([1.5, 1, 5], center=true);
    }
    // Side edges
    for(y = [-22.5:2.5:22.5]) {
        translate([24.5, y, 2.5])
            cube([1, 1.5, 5], center=true);
        translate([-24.5, y, 2.5])
            cube([1, 1.5, 5], center=true);
