    // Rounded head
    translate([-20, 0, 0])
        scale([1.3, 1, 0.8])
            sphere(d=20, $fn=32);
}

// Beak module
module cookie_beak() {
    // Small triangular beak
    translate([-30, 0, -2])
        rotate([0, 0, -90])
            cylinder(h=12, d1=6, d2=0, $fn=3);
}

// Tail feathers module
module cookie_tail() {
    // Fan tail
    for(i=[-2:2]) {
        rotate([0, 0, i*20])
            translate([22, i*3, 0])
                scale([1.5, 0.3, 1])
                    cylinder(h=15, d=8, $fn=8);
    }
}

// Wing detail texture
module wing_texture() {
    // Feather lines on wings
    for(i=[-5:5]) {
        translate([10, i*2.5, base_thickness-0.5])
            rotate([0, 0, 15])
                scale([1, 0.1, 0.5])
                    cube([20, 1, 1]);
    }
}

// Body texture pattern
module body_texture() {
    // Spot pattern
    for(i=[-2:2]) {
        for(j=[-3:3]) {
            if((i+j)%2==0) {
                translate([i*8, j*6, base_thickness-0.5])
                    scale([1, 0.8, 0.3])
                        sphere(d=3, $fn=16);
            }
        }
    }
}

// Eye details
module cookie_eyes() {
    // Left eye
    translate([-25, 5, 2])
        color(color_dark_brown)
            sphere(d=3, $fn=16);

    // Right eye
    translate([-25, -5, 2])
        color(color_dark_brown)
            sphere(d=3, $fn=16);
}

// Main assembly
color(color_light_brown) {
    union() {
        // Base cookie
        cookie_body();
        cookie_head();
        cookie_beak();
        cookie_tail();

        // Add texture details
        color(color_brown) {
            wing_texture();
            body_texture();
        }

        // Eyes
        cookie_eyes();

        // Bottom support ring for stability
        translate([0, 0, -base_thickness/2])
            cylinder(h=base_thickness/2, d=50, $fn=32);
    }
}

// Mississippi Mud Pie - 3D printable food model
// Features chocolate crust, creamy filling, chocolate shavings and whipped cream

$fn = 30;  // Smooth curves for 3D printing

// Main dimensions (mm)
pie_diameter = 80;
pie_height = 25;
crust_thickness = 4;
filling_height = 15;
whip_height = 6;

// Colors
chocolate_color = [0.4, 0.2, 0.1];
cream_color = [0.95, 0.9, 0.85];
dark_chocolate = [0.25, 0.12, 0.06];

// Chocolate cookie crust
module crust() {
    color(chocolate_color) {
        difference() {
            cylinder(h = pie_height, r1 = pie_diameter/2, r2 = pie_diameter/2 * 0.95);
            translate([0, 0, crust_thickness])
                cylinder(h = pie_height, r1 = pie_diameter/2 - 8, r2 = pie_diameter/2 * 0.95 - 8);
        }
    }
}

// Creamy chocolate filling with slight dome
module filling() {
    color(cream_color) {
        translate([0, 0, crust_thickness])
            cylinder(h = filling_height, r1 = pie_diameter/2 - 8.5, r2 = pie_diameter/2 - 8);

        // Create gentle dome top
        translate([0, 0, crust_thickness + filling_height - 2])
            scale([1, 1, 0.3])
                sphere(d = pie_diameter - 17);
    }
}

// Individual chocolate shaving
module chocolate_shaving() {
    color(dark_chocolate) {
        scale([8, 3, 0.5])
            sphere(d = 1);
    }
}

// Sprinkle chocolate shavings on top
module chocolate_shavings() {
    for(i = [0:35]) {
        angle = i * 137.5;  // Golden angle for natural distribution
        radius = (pie_diameter/2 - 15) * sqrt(i/35);
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            crust_thickness + filling_height + 1
        ]) {
            rotate([0, 0, angle + rand(i) * 30])
                chocolate_shaving();
        }
    }
}

// Whipped cream rosette
module whipped_rosette() {
    color(cream_color) {
        for(i = [0:5]) {
            rotate([0, 0, i * 60])
                translate([3, 0, 0])
                    rotate([90, 0, 0])
                        cylinder(h = 2, r1 = 2.5, r2 = 1.5);
        }
        cylinder(h = whip_height, r = 3);
    }
}

// Place whipped cream dollops
module whipped_cream() {
    positions = [
        [0, 0],
        [15, 10],
        [-18, -8],
        [12, -15],
        [-10, 15]
    ];

    for(pos = positions) {
        translate([pos[0], pos[1], crust_thickness + filling_height + 2])
            whipped_rosette();
    }
}

// Utility function for random variation
function rand(n) = (n * 73.3) % 1;

// Assemble the pie
union() {
    crust();
    filling();
    chocolate_shavings();
    whipped_cream();
}

// California Avocado with realistic appearance
// Medium complexity 3D printable model

// Overall dimensions: ~60mm x 40mm x 45mm

$fn = 32;  // Smooth curves

// Main avocado body - slightly asymmetric organic shape
module avocado_body() {
    color([0.25, 0.45, 0.15])  // Dark green skin
    union() {
        // Main body - ellipsoid shape
        scale([1.2, 1, 1.1]) 
        sphere(20);

        // Bottom bulge
        translate([0, 0, -15])
        scale([1.1, 0.9, 0.7])
        sphere(15);

        // Top taper
        translate([0, 0, 18])
        scale([0.7, 0.8, 0.6])
        sphere(10);
    }
}

// Skin texture - bumpy surface pattern
module skin_texture() {
    color([0.2, 0.4, 0.12])
    difference() {
        avocado_body();

        // Small indentations for realistic skin texture
        for(i = [0:15]) {
            rotate([0, 0, i * 24])
            translate([18 + sin(i * 30) * 3, 0, 5 + cos(i * 20) * 8])
            rotate([90, 0, 0])
            scale([1, 1, 0.3])
            cylinder(h = 2, r1 = 1.5, r2 = 0.5);
        }

        for(i = [0:12]) {
            rotate([0, 0, i * 30 + 15])
            translate([16 + sin(i * 25) * 2, 0, -5 + cos(i * 15) * 6])
            rotate([90, 0, 0])
            scale([1, 1, 0.3])
            cylinder(h = 1.5, r1 = 1.2, r2 = 0.4);
        }
    }
}

// Inner flesh - light green
module avocado_flesh() {
    color([0.7, 0.85, 0.4])  // Light green flesh
    difference() {
        // Main flesh body
        scale([1.1, 0.9, 1])
        translate([0, 0, -3])
        sphere(17);

        // Seed cavity
        scale([0.7, 0.7, 0.9])
        sphere(12);
    }
}

// Seed (pit) - brown oval
module seed() {
    color([0.4, 0.25, 0.15])  // Brown seed
    scale([0.9, 0.7, 1.2])
    sphere(11);

    // Seed texture
    color([0.35, 0.2, 0.1])
    for(i = [0:8]) {
        rotate([0, 0, i * 45])
        translate([5 + sin(i * 20) * 2, 0, 0])
        scale([0.1, 0.1, 1.2])
        sphere(11);
    }
}

// Cut surface detail
module cut_surface() {
    color([0.75, 0.9, 0.45])  // Brighter exposed flesh

    // Flat cut plane
    translate([0, 0, 2])
    cube([45, 45, 0.5], center = true);

    // Flesh pattern
    for(x = [-15:5:15]) {
        for(y = [-15:5:15]) {
            if(sqrt(x*x + y*y) < 20) {
                translate([x, y, 2.3])
                scale([0.8, 0.8, 0.3])
                sphere(1.5);
            }
        }
    }
}

// Stem at top
module stem() {
    color([0.3, 0.5, 0.2])
    translate([0, 0, 32])
    rotate([0, 0, 5])
    cylinder(h = 5, r1 = 2.5, r2 = 2);
}

// Assembly
union() {
    // Right half - whole avocado
    translate([15, 0, 0]) {
        skin_texture();
        stem();
    }

    // Left half - cut open
    translate([-15, 0, 0]) {
        difference() {
            skin_texture();
            translate([0, 0, 0])
            cube([50, 50, 50], center = true);
        }

        avocado_flesh();
        seed();
        cut_surface();
    }
}

