// Pinkeye (Purple Hull Pea) - realistic style, medium complexity
// Overall size: ~70mm x 50mm x 25mm

// Main pea pod with slight curve
module pea_pod() {
    color([0.15, 0.35, 0.10])  // Dark green pod color
    union() {
        // Main pod body
        hull();

        // Pod seam
        seam();

        // Add slight texture with small bumps
        texture();
    }
}

// Main hull shape
module hull() {
    difference() {
        // Curved main body
        rotate([0, -10, 0]) 
        scale([1, 0.6, 0.4])
        hull() {
            translate([0, 0, 0])
            sphere(d=50, $fn=60);
            translate([20, 0, 0])
            sphere(d=45, $fn=60);
        }

        // Hollow out inside
        rotate([0, -10, 0])
        scale([0.85, 0.5, 0.35])
        hull() {
            translate([0, 0, 0])
            sphere(d=50, $fn=60);
            translate([20, 0, 0])
            sphere(d=45, $fn=60);
        }

        // Split opening
        translate([-10, 0, 0])
        cube([40, 60, 30], center=true);
    }
}

// Pod seam line
module seam() {
    color([0.10, 0.25, 0.08])
    for(i = [-20:2:20]) {
        translate([i, 0, 11])
        rotate([90, 0, 0])
        cylinder(h=0.5, d1=2, d2=1, $fn=6);
    }
}

// Surface texture
module texture() {
    color([0.12, 0.30, 0.09])
    for(i = [-15:5:15]) {
        for(j = [-8:4:8]) {
            translate([i, j, 11])
            sphere(d=1.5, $fn=8);
        }
    }
}

// Purple hull peas inside
